{{#if:{{#switch:{{{1|}}}|n.d.|不明=1|#default={{#switch:{{{2|}}}|n.d.|不明=1|#default=}}}}
 |n.d.
 |{{#if:{{#ifeq:{{padleft:|2|{{{1|}}}X}}|{{padleft:|2|[[X]]}}|1|}}{{#if:{{{3|}}}|{{{1|}}}{{#if:{{{2|}}}||1}}|}}{{#if:{{{2|}}}|{{#ifeq:{{padleft:|5|{{{2}}}A}}|{{padleft:|4|{{{2}}}B}}年|1|}}{{#switch:{{padleft:|2|{{{2}}}X}}|平成|昭和|大正|明治=1|#default=}}{{#ifeq:{{padleft:|2|{{{2}}}X}}|{{padleft:|2|[[X]]}}|1|}}|}}{{#if:{{{isodate|}}}|{{#if:{{{1|}}}{{{2|}}}||1}}|}}
  |{{{1|}}} {{{3|}}} {{#if:{{{2|}}}|{{#ifeq:{{{2}}}|{{padleft:|4|{{{2}}}X}}年|{{padleft:|4|{{{2}}}}}|{{{2}}}}}|}}{{#if:{{NAMESPACE}}||[[Category:テンプレート呼び出しエラーのあるページ/調査中/Cite web (冗長日付){{!}}{{padleft:{{PAGESIZE:{{PAGENAME}}|R}}|6|0}}}}]]}}<!-- {{Citation/showdateError|year={{{2|}}}|date={{{1|}}}|month={{{3|}}}}} -->
  |{{#if:{{{1|}}}
   |{{#if:{{{3|}}}
    |{{Citation/showdateError|year={{{2|}}}|date={{{1|}}}|month={{{3|}}}}}
    |{{{1}}}
   }}
   |{{#if:{{{3|}}}
    |{{#iferror:{{#time:Ym|1 {{{3}}} {{padleft:|4|{{{2}}}}}}}<!--
  -->|{{#iferror:{{#time:Ym|{{padleft:|4|{{{2}}}}}-{{{3}}}-1}}
       |{{Citation/showdateError|year={{{2|}}}|month={{{3|}}}}}
       |{{{2}}}-{{{3}}}
      }}
     |{{{3}}} {{{2|}}}
    }}
    |{{{2|}}}<!-- DEFAULT -->
   }}
  }}
 }}
}}<noinclude>
