-- Make aliases work the same as normal keys, and remove the "aliases" subtables. 
for k, t in pairs(data) do
	if t.aliases then
		for i, alias in ipairs(t.aliases) do
			data[alias] = t
		end
		t.aliases = nil
	end
	data[k] = t
end

return data

